-- 1.6 - Populate “capture_date” field with the autogenerated dates from the updates table corresponding to the date that information in a specific row was updated in the CABD. 
CREATE FUNCTION cabd.updatelastmodified() RETURNS TRIGGER
    LANGUAGE plpgsql
    AS $$
BEGIN
	NEW.last_modified := current_timestamp;
  	RETURN NEW;
END;
$$;

CREATE trigger dams_last_modified_trg BEFORE INSERT OR UPDATE ON dams.dams_medium_large FOR EACH ROW EXECUTE PROCEDURE cabd.updatelastmodified();
CREATE trigger waterfalls_last_modified_trg BEFORE INSERT OR UPDATE ON waterfalls.waterfalls FOR EACH ROW EXECUTE PROCEDURE cabd.updatelastmodified();

